 stock = uiState.gainersList[0],
 stockType = StockType.Active(),
 onCompanyClick = {}
 )
 StockItem(
 modifier = Modifier.weight(1f),
 stock = uiState.gainersList[1],
 stockType = StockType.Active(),
 onCompanyClick = {}
 )
 }
 Row(
 modifier = Modifier
 .fillMaxSize()
 .padding(top = 8.sdp, start=8.sdp, end=8.sdp)
