 )
 {
 StockItem(
 modifier = Modifier.weight(1f),
 stock = uiState.gainersList[2],
 stockType = StockType.Active(),
 onCompanyClick = {}
 )
 SeeMoreCard(
 title1 = uiState.gainersList[3].ticker,
 title2 = uiState.gainersList[4].ticker,
 title3 = uiState.gainersList[5].ticker,
 title4 = uiState.gainersList[6].ticker,
 modifier = Modifier.weight(1f),
 onClick = {onNavigationToDisplay()}
 )
 }
 }
 uiState.isLoading -> {
 HomeScreenShimmer()
 }
 else -> {

 }
 }
 when{
 !uiState.isLoading && uiState.losersList!=emptyList<Stock>()-> {
