 when (response) {
 is Resource.Loading -> {
 _uiState.update { it.copy(isLoading = true) }
 }

 is Resource.Error -> {
 _uiState.update {
 it.copy(isLoading = false, error = response.message)
 }
 }

 is Resource.Success -> {
 _uiState.update {
 it.copy(
 isLoading = false,
 companyDetails = response.data,
 error = null
 )
 }
 }
 }
 }
 }

