@Composable
fun ThemeSelector() {
 var expanded by remember { mutableStateOf(false) }
 var selectedTheme by remember { mutableStateOf(ThemeMode.SYSTEM) }

 fun getThemeIcon(theme: ThemeMode): Int {
 return when (theme) {
 ThemeMode.LIGHT -> R.drawable.ic_light_mode
 ThemeMode.DARK -> R.drawable.ic_dark_mode
 ThemeMode.SYSTEM -> R.drawable.ic_system_default
 }
 }

 Column {
 Box {
 Icon(
 painter= painterResource(getThemeIcon(selectedTheme)),
 contentDescription = "Theme",
 tint = Color.White,
 modifier = Modifier
 .size(32.dp)
 .clickable { expanded = true }
 .padding(4.dp)
 .background(colorResource(R.color.bg_neutral))
 )

 // Dropdown menu
 DropdownMenu(
 expanded = expanded,
 onDismissRequest = { expanded = false },
 offset = DpOffset(x = (-16).dp, y = 4.dp),
 modifier = Modifier
 .clip(RoundedCornerShape(12.dp))
 .background(colorResource(R.color.bg_neutral))
 .border(
 width = 2.dp,
 color = colorResource(R.color.white).copy(alpha = 0.3f),
 shape = RoundedCornerShape(12.dp)
 )
 .padding(horizontal = 12.dp, vertical = 8.dp)
 ) {
 ThemeMode.values().forEachIndexed { index, theme ->
 DropdownMenuItem(
 leadingIcon = {
 Icon(
 painter = painterResource(getThemeIcon(theme)),
 tint = Color.White,
 contentDescription = null,
 modifier = Modifier.size(22.dp)
 )
 },
 text = { Text(text = theme.displayName, color = Color.White) },
 onClick = {
 selectedTheme = theme
 AppCompatDelegate.setDefaultNightMode(theme.nightMode)
 expanded = false
 }
 )
 if (index < ThemeMode.values().size - 1) {
 HorizontalDivider(
 color = colorResource(R.color.white).copy(alpha = 0.1f)
 )
 }
 }
 }
 }
 }
}

