 onValueChange = onSearchTextChange,
 leadingIcon = {
 Icon(
 imageVector = Icons.AutoMirrored.Filled.ArrowBack,
 contentDescription = "Back",
 modifier = Modifier
 .size(24.dp)
 .clickable {
 navigateBack()
