 if (uiState.isLoading) {
 ProgressBar()
 } else {
 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .background(colorResource(R.color.white)),
 contentPadding = PaddingValues(horizontal = 16.sdp, vertical = 8.sdp),
