 Text(
 text = "Recent Searches",
 color = colorResource(R.color.black),
 fontSize = 18.sp,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(vertical = 12.sdp, horizontal = 4.sdp)
 )
 }
 }
